"use strict";

for (num = 1; num <= 100; num++) {
  if (num % 7 == 0) {
    console.log('boom' + ' ' + num);
  } else {
    if (num > 69 && num < 80) {
      console.log('boom' + ' ' + num);
    } else {
      if (num % 10 == 7) {
        console.log('boom' + ' ' + num);
      } else {
        console.log(num);
      }
    }
  }
}